var flow = null;

creation = () => {
    clear();
    for(let i = 0; i < particle.length; i++) {
        particle[i].F=[0, 0];
        for(let j = 0; j < particle.length; j++){
            if(j==i) continue;
            particle[i].F[0] += particle[j].m*particle[i].m*Force(d(i, j, particle))*(particle[j].x-particle[i].x)/d(i, j, particle);
            particle[i].F[1] += particle[j].m*particle[i].m*Force(d(i, j, particle))*(particle[j].y-particle[i].y)/d(i, j, particle);
        }

    }
    particle.forEach(item => {
        item.v[0] += dt*item.F[0]/item.m;
        item.v[1] += dt*item.F[1]/item.m;

        item.x += 0.5*item.v[0];
        item.y += 0.5*item.v[1];

        if(item.x < 10) item.v[0] *= -1;
        if(item.y < 10) item.v[1] *= -1;
        if(item.x > Canvas.width-5) item.v[0] *= -1;
        if(item.y > Canvas.height-5) item.v[1] *= -1;
        
        item.draw();
    });
}

start_time = () => { flow = setInterval(creation, 100) }

stop_time = () => { clearInterval(flow) }